mapper.update:
UPDATE 
	sec_roles
SET 
	name=#{name} 
WHERE 
	id=#{id}

mapper.select:
SELECT * FROM sec_roles
AND ( name LIKE CONCAT('%', #{condition}, '%') or value LIKE CONCAT('%', #{condition}, '%') )
limit #{offset}, #{rows}

SELECT a.user_role_rela_id, b.* FROM sec_user_role_rela a, sec_roles b WHERE a.user_id = #{accountId} AND a.role_id = b.id
AND ( name LIKE CONCAT('%', #{condition}, '%') or value LIKE CONCAT('%', #{condition}, '%') )
limit #{offset}, #{rows}

SELECT c.* FROM sec_roles c WHERE c.id NOT IN (SELECT a.id FROM sec_roles a, sec_user_role_rela b WHERE a.id = b.role_id AND b.user_id = #{accountId})
AND ( c.name LIKE CONCAT('%', #{condition}, '%') or c.value LIKE CONCAT('%', #{condition}, '%') )
limit #{offset}, #{rows}
